mapper.insert:

    SELECT id
    FROM CUSTOMER
    WHERE email = #{email}
    AND name =#{name}

INSERT INTO CUSTOMER( email, password, name, phone, created_date, modified_date)
VALUES ( #{email}, #{password}, #{name}, #{phone}, now(), now());

mapper.select:
SELECT EXISTS (
SELECT 1
FROM CUSTOMER
WHERE EMAIL = #{email}
)

mapper.update:

    SELECT name,
           phone
     FROM CUSTOMER
    WHERE id = #{id}
      AND name =#{name}

UPDATE CUSTOMER SET name = #{name}, phone = #{phone}, modified_date = now()
WHERE id = #{id} AND email = #{email}
