mapper.insert:

    SELECT id
      FROM SUPPLIER
     WHERE email = #{email}
       AND name =#{name}
       AND company_name = #{companyName}
       AND business_reg_no = #{businessRegNo}

INSERT INTO SUPPLIER( email, password, name, company_name, business_reg_no, created_date, modified_date, role)
VALUES ( #{email}, #{password}, #{name}, #{companyName}, #{businessRegNo}, now(), now(), #{role});

mapper.select:
SELECT EXISTS (
                SELECT 1
                  FROM SUPPLIER
                 WHERE EMAIL = #{email}
              )
